import{_ as e,b as t,a,c as i}from"./chunks/search-panel.9e61f3cc.js";import{_ as r}from"./chunks/settings-context-menu.8558db04.js";import{_ as n,o as s,c as h,a as l}from"./app.3c3613bb.js";const x=JSON.parse('{"title":"Usage","description":"","frontmatter":{},"headers":[{"level":2,"title":"Getting started","slug":"getting-started","link":"#getting-started","children":[]},{"level":2,"title":"Features","slug":"features","link":"#features","children":[{"level":3,"title":"Parameter size warning","slug":"parameter-size-warning","link":"#parameter-size-warning","children":[]}]},{"level":2,"title":"Document size warning","slug":"document-size-warning","link":"#document-size-warning","children":[]},{"level":2,"title":"Search","slug":"search","link":"#search","children":[]}],"relativePath":"usage.md"}'),o={name:"usage.md"},d=l('<h1 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-hidden="true">#</a></h1><h2 id="getting-started" tabindex="-1">Getting started <a class="header-anchor" href="#getting-started" aria-hidden="true">#</a></h2><p>Once installed, you can verify the plugin was loaded correctly by checking for the widget in:</p><p><code>Display -&gt; Widgets -&gt; Size Analyzer</code></p><p><img src="'+e+'" alt="Display widgets menu"></p><p>There will be no other indication of the widget other than this, as we tried to make it as unobtrusive to the user as possible.</p><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-hidden="true">#</a></h2><p>The widget will display one of 2 warnings when the internal data exceeds the threshold.</p><h3 id="parameter-size-warning" tabindex="-1">Parameter size warning <a class="header-anchor" href="#parameter-size-warning" aria-hidden="true">#</a></h3><p><img src="'+t+'" alt="Parameter warning"></p><p>This warning will be displayed on the top-corner of each parameter that has a bigger size than the threshold.</p><p>When hovered, it will display a tooltip explaining what it is, as well as the current size of that parameter and the configured threshold.</p><p>The threshold can be modified by right-clicking the warning.</p><p><img src="'+r+'" alt="Parameter warning context menu"></p><h2 id="document-size-warning" tabindex="-1">Document size warning <a class="header-anchor" href="#document-size-warning" aria-hidden="true">#</a></h2><p><img src="'+a+'" alt="Document warning"></p><p>This warning will be displayed on the bottom-left corner of the Grasshopper canvas.</p><p>When hovered, it will display a tooltip explaining what it is, as well as the current threshold set for the document.</p><p>Just as with the <em>parameter warning</em>, right-clicking will display a context menu that will allow the user to edit the thresholds.</p><h2 id="search" tabindex="-1">Search <a class="header-anchor" href="#search" aria-hidden="true">#</a></h2><p><img src="'+i+'" alt="Search panel"></p><p>The search panel is currently only accessible via the context-menu of each warning, by clicking <code>Open search dialog</code></p><p>This command will open a new pop-up window that will display a list of all parameters that currently exceed the specified threshold, isolating them from the rest of the components in the document.</p><p>It will display the size of each parameter as well as it&#39;s type, and when clicked the canvas will be focused on the specific parameter, as well as highlighted in red to better distinguish them in &quot;busy&quot; areas of the document.</p>',24),c=[d];function p(g,m,u,w,f,_){return s(),h("div",null,c)}const v=n(o,[["render",p]]);export{x as __pageData,v as default};
